import React, { useEffect, useState } from "react";
import axios from "axios";

const App = () => {
  const [clients, setClients] = useState([]);
  const [newClient, setNewClient] = useState({
    firstname: "",
    secondname: "",
    email: "",
    phone: "",
    city: "",
    country: "",
  });
  const [error, setError] = useState(null); // For displaying errors
  const [success, setSuccess] = useState(false); // For showing success messages

  useEffect(() => {
    axios.get("http://127.0.0.1:5000/clients")
      .then(response => setClients(response.data))
      .catch(error => console.error("Error fetching clients:", error));
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewClient(prevState => ({ ...prevState, [name]: value }));
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    setError(null); // Clear previous errors
    setSuccess(false); // Reset success state

    axios.post("http://127.0.0.1:5000/clients", newClient)
      .then(response => {
        setClients(prevClients => [...prevClients, { ...newClient, id: response.data.id }]);
        setNewClient({ firstname: "", secondname: "", email: "", phone: "", city: "", country: "" });
        setSuccess(true); // Show success message
      })
      .catch(error => {
        console.error("Error adding client:", error);
        setError("Failed to add client. Please try again.");
      });
  };

  return (
    <div>
      <h1>Clients</h1>
      <ul>
        {clients.map(client => {
          const fullName = [client.firstname, client.secondname]
            .filter(name => name)
            .join(" ") || "Unnamed Client";

          return (
            <li key={client.id}>
              {fullName}
              {client.email && ` - ${client.email}`}
            </li>
          );
        })}
      </ul>

      <div style={{ marginTop: "20px" }}>
        <h2>Add New Client</h2>
        {error && <p style={{ color: "red" }}>{error}</p>}
        {success && <p style={{ color: "green" }}>Client added successfully!</p>}
        <form onSubmit={handleFormSubmit}>
          <input
            type="text"
            name="firstname"
            placeholder="First Name"
            value={newClient.firstname}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="secondname"
            placeholder="Second Name"
            value={newClient.secondname}
            onChange={handleInputChange}
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={newClient.email}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="phone"
            placeholder="Phone"
            value={newClient.phone}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="city"
            placeholder="City"
            value={newClient.city}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="country"
            placeholder="Country"
            value={newClient.country}
            onChange={handleInputChange}
          />
          <button type="submit">Add Client</button>
        </form>
      </div>
    </div>
  );
};

export default App;
