import React, { useEffect, useState } from "react";
import axios from "axios";

const App = () => {
  const [clients, setClients] = useState([]);

  useEffect(() => {
    axios.get("http://127.0.0.1:5000/clients")
      .then(response => {
        setClients(response.data);
      })
      .catch(error => console.error("Error fetching clients:", error));
  }, []);

  return (
    <div>
      <h1>Clients</h1>
      <ul>
        {clients.map(client => {
          // Construct the full name if available
          const fullName = [client.firstname, client.secondname]
            .filter(name => name) // Remove empty strings
            .join(" "); // Join with a space

          // Construct the display text
          const displayText = fullName || "Unnamed Client";

          return (
            <li key={client.id}>
              {displayText}
              {client.email && ` - ${client.email}`} {/* Append email if available */}
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default App;
