import React, { useEffect, useState } from "react";
import axios from "axios";

const App = () => {
  const [clients, setClients] = useState([]); // List of clients
  const [selectedClient, setSelectedClient] = useState(null); // Track selected client
  const [newClient, setNewClient] = useState({
    firstname: "",
    secondname: "",
    email: "",
    phone: "",
    city: "",
    country: "",
  }); // Form state for adding a client
const [addSuccess, setAddSuccess] = useState(false); // Success message for adding a client
const [editSuccess, setEditSuccess] = useState(false); // Success message for editing a client
const [error, setError] = useState(null); // Generic error message

  useEffect(() => {
    // Fetch initial list of clients
    axios.get("http://127.0.0.1:5000/clients")
      .then(response => setClients(response.data))
      .catch(error => console.error("Error fetching clients:", error));
  }, []);

  const handleClientClick = (client) => {
    // Set the clicked client as selected
    setSelectedClient(client);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewClient(prevState => ({ ...prevState, [name]: value }));
  };

  const handleFormSubmit = (e) => {
  e.preventDefault();
  setError(null);
  setAddSuccess(false); // Reset add success message
  setEditSuccess(false); // Clear edit success message if it was showing

  axios.post("http://127.0.0.1:5000/clients", newClient)
    .then(response => {
      setClients(prevClients => [...prevClients, { ...newClient, id: response.data.id }]);
      setNewClient({ firstname: "", secondname: "", email: "", phone: "", city: "", country: "" });
      setAddSuccess(true); // Show success message for adding
    })
    .catch(error => {
      console.error("Error adding client:", error);
      setError("Failed to add client. Please try again.");
    });
};


  const [editMode, setEditMode] = useState(false); // Track if editing is enabled

const handleEditSubmit = (e) => {
  e.preventDefault();
  setError(null);
  setEditSuccess(false); // Reset edit success message
  setAddSuccess(false); // Clear add success message if it was showing

  axios.put(`http://127.0.0.1:5000/clients/${selectedClient.id}`, selectedClient)
    .then(response => {
      setClients(prevClients =>
        prevClients.map(client =>
          client.id === selectedClient.id ? { ...response.data } : client
        )
      );
      setEditMode(false); // Exit edit mode
      setEditSuccess(true); // Show success message for editing
    })
    .catch(error => {
      console.error("Error updating client:", error);
      setError("Failed to update client. Please try again.");
    });
};



const handleDeleteClient = (id) => {
  axios.delete(`http://127.0.0.1:5000/clients/${id}`)
    .then(() => {
      setClients(prevClients => prevClients.filter(client => client.id !== id));
      if (selectedClient?.id === id) setSelectedClient(null); // Clear details if deleted
    })
    .catch(error => console.error("Error deleting client:", error));
};



  return (
    <div>
      <h1>Clients</h1>
      <ul>
        {clients.map(client => {
          const fullName = [client.firstname, client.secondname]
            .filter(name => name) // Remove empty names
            .join(" ") || "Unnamed Client";

          return (
              <li
                  key={client.id}
                  onClick={() => handleClientClick(client)}
                  style={{cursor: "pointer", marginBottom: "5px"}}
              >
                {fullName}
                {client.email && ` - ${client.email}`}
                <button onClick={(e) => {
                  e.stopPropagation();
                  handleDeleteClient(client.id);
                }}>Delete
                </button>
              </li>
          );
        })}
      </ul>

      {selectedClient && (
  <div style={{ marginTop: "20px", padding: "10px", border: "1px solid #ccc" }}>
    <h2>Client Details</h2>
    {editMode ? (
      <form onSubmit={handleEditSubmit}>
        <input
          type="text"
          name="firstname"
          value={selectedClient.firstname || ""}
          onChange={(e) =>
            setSelectedClient({ ...selectedClient, firstname: e.target.value })
          }
        />
        <input
          type="text"
          name="secondname"
          value={selectedClient.secondname || ""}
          onChange={(e) =>
            setSelectedClient({ ...selectedClient, secondname: e.target.value })
          }
        />
        <input
          type="email"
          name="email"
          value={selectedClient.email || ""}
          onChange={(e) =>
            setSelectedClient({ ...selectedClient, email: e.target.value })
          }
        />
        <input
          type="text"
          name="phone"
          value={selectedClient.phone || ""}
          onChange={(e) =>
            setSelectedClient({ ...selectedClient, phone: e.target.value })
          }
        />
        <input
          type="text"
          name="city"
          value={selectedClient.city || ""}
          onChange={(e) =>
            setSelectedClient({ ...selectedClient, city: e.target.value })
          }
        />
        <input
          type="text"
          name="country"
          value={selectedClient.country || ""}
          onChange={(e) =>
            setSelectedClient({ ...selectedClient, country: e.target.value })
          }
        />
        <button type="submit">Save Changes</button>
      </form>
    ) : (
      <>
        <p><strong>Name:</strong> {selectedClient.firstname} {selectedClient.secondname}</p>
        <p><strong>Email:</strong> {selectedClient.email || "Not available"}</p>
        <p><strong>Phone:</strong> {selectedClient.phone || "Not available"}</p>
        <p><strong>City:</strong> {selectedClient.city || "Not available"}</p>
        <p><strong>Country:</strong> {selectedClient.country || "Not available"}</p>
        <button onClick={() => setEditMode(true)}>Edit</button>
      </>
    )}
  </div>
)}



      <div style={{ marginTop: "20px" }}>
        <h2>Add New Client</h2>
        {error && <p style={{ color: "red" }}>{error}</p>}
        {success && <p style={{ color: "green" }}>Client added successfully!</p>}
        <form onSubmit={handleFormSubmit}>
          <input
            type="text"
            name="firstname"
            placeholder="First Name"
            value={newClient.firstname}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="secondname"
            placeholder="Second Name"
            value={newClient.secondname}
            onChange={handleInputChange}
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={newClient.email}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="phone"
            placeholder="Phone"
            value={newClient.phone}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="city"
            placeholder="City"
            value={newClient.city}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="country"
            placeholder="Country"
            value={newClient.country}
            onChange={handleInputChange}
          />
          <button type="submit">Add Client</button>
        </form>
      </div>
    </div>
  );
};

export default App;
